package it.twt.tests;import com.codeborne.selenide.Configuration;import org.junit.jupiter.api.BeforeAll;import org.junit.jupiter.api.Test;import static com.codeborne.selenide.Condition.text;import static com.codeborne.selenide.Selenide.$;import static com.codeborne.selenide.Selenide.open;public class TextBoxTests {    @BeforeAll    static void beforeAll() {        Configuration.browserSize = "2048x1024";    }    @Test    void fillFormTest() {        String name = "Ilia";        String email = "ilia@twt.it";        String cAdress = "coraso Monte San Gabriele";        String pAdress = "via Matteo Maria Boiardo";        open("https://demoqa.com/text-box");        $("#userName").setValue(name);        $("#userEmail").setValue(email);        $("#currentAddress").setValue(cAdress);        $("#permanentAddress").setValue(pAdress);        $("#submit").click();        $("#output #name").shouldHave(text(name));        $("#output #email").shouldHave(text(email));        $("#output p#currentAddress").shouldHave(text(cAdress));        $("#output #permanentAddress").shouldHave(text(pAdress));    }}